import React, { Component } from 'react'
import CodeMirrorEditor from './CodeMirrorEditor';

class Editor extends Component{

    constructor(props) {
        super(props);
        this.state = {
            code: '',
            inputs: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.inputs);
        event.preventDefault();
    }

    handleChange(event) {
        this.setState({code: event.target.value});
        this.setState({inputs: event.target.value});
    }

    render() {
        return (
            <div>
                <center><h1>Python Interpreter</h1></center>
                <form onSubmit={this.handleSubmit}>
                    <div id="editor"><CodeMirrorEditor /></div>
                    <center><input type = "submit" className="btn btn-primary btn-lg" value = "Run" /></center>
                    <button onClick={this.handleSubmit}></button>
                    <center>
                        <div className="textAreaColumn">
                            <div>
                                <span><h2>Input</h2></span>
                                <textarea id = "inputs" cols="150" rows="10" defaultValue="" value={this.state.inputs} onChange={this.handleChange}></textarea>
                            </div>
                            <div>
                                <span><h2>Output</h2></span>
                                <textarea readOnly cols="150" rows="10" id="output"></textarea>
                            </div>
                        </div>
                    </center>
                </form>    
            </div>
        );
    }
}

export default Editor;
