import React, { Component } from 'react'
import CodeMirrorEditor from './CodeMirrorEditor';
import { w3cwebsocket as W3CWebSocket } from "websocket";

const client = new W3CWebSocket('ws://127.0.0.1:8765');

class Editor extends Component{
    componentWillMount() {
        client.onopen = () => {
          console.log('WebSocket Client Connected');
        };
        client.onmessage = (message) => {
          console.log(message);
        };
    }
    /*handleSubmit = () => {
        const connection = new WebSocket('ws://localhost:8765');
        connection.onopen = () => {
            console.log('connected');
        };

        connection.onclose = () => {
            console.error('disconnected');
        };

        connection.onerror = error => {
            console.error('failed to connect', error);
        };

        document.querySelector('form').addEventListener('submit', event => {
            event.preventDefault();
            let message = document.querySelector('#editor').value;
            let inputs = document.querySelector('#inputs').value;
            console.log(message+inputs);
            connection.send(message);
        });

        connection.onmessage = (event) => {
            document.getElementById("output").innerHTML = event.data;
        }
        //<div id="editor"><CodeMirrorEditor />
        //</div><textarea id = "editor">print("abhishek")</textarea>
    }*/
    render() {
        return (
            <div>
                <center><h1>Python Interpreter</h1></center>
                <form onSubmit={this.handleSubmit}>
                    <div id="editor"><CodeMirrorEditor /></div>
                    <center><input type = "submit" className="btn btn-primary btn-lg" value = "Run" /></center>
                    <button onClick={this.handleSubmit}></button>
                    <center>
                        <div className="textAreaColumn">
                            <div>
                                <span><h2>Input</h2></span>
                                <textarea id = "inputs" cols="150" rows="10" defaultValue=""></textarea>
                            </div>
                            <div>
                                <span><h2>Output</h2></span>
                                <textarea readOnly cols="150" rows="10" id="output"></textarea>
                            </div>
                        </div>
                    </center>
                </form>    
            </div>
        );
    }
}

export default Editor;
