import React, { Component, useRef, useEffect } from 'react'
import CodeMirrorEditor from './CodeMirrorEditor';
import {UnControlled as CodeMirror} from 'react-codemirror2'
import 'codemirror/lib/codemirror.css';
require('codemirror/mode/python/python');

class Editor extends Component{

    constructor(props) {
        super(props);
        this.state = {
            code: 'default text',
            inputs: 'sample input'
        };
        /*this.handleChange = this.handleChange.bind(this);*/
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleCodeChange = event => {
        this.setState({ code: event.target.value });
    }

    handleInputChange = event => {
        this.setState({ inputs: event.target.value });
    }

    handleSubmit(event) {
        const { code, inputs } = this.state
        event.preventDefault();
        alert(`Code is ${code}\nInputs are ${inputs}`);

    }

    render() {
        return (
            <div>
                <center><h1>Python Interpreter</h1></center>
                <form onSubmit={this.handleSubmit}>
                    <div id="editor" >
                    <CodeMirror
                    value='print("Hello world!")'
                    options={{
                        mode: 'python',
                        lineNumbers: true,
                        smartIndent: true,
                    }}
                    onChange={this.handleCodeChange}
                />
                    </div>

                    <center>
                        <input 
                            type = "submit" 
                            className="btn btn-primary btn-lg" 
                            value = "Run" 
                        />
                    </center>

                    <button onClick={this.handleSubmit}></button>

                    <center>
                        <div className="textAreaColumn">
                            <div>
                                <span><h2>Input</h2></span>
                                <textarea 
                                    id = "inputs" 
                                    cols="150" 
                                    rows="10" 
                                    defaultValue="" 
                                    value={this.state.inputs} 
                                    onChange={this.handleInputChange}
                                ></textarea>
                            </div>
                            <div>
                                <span><h2>Output</h2></span>
                                <textarea 
                                    readOnly 
                                    cols="150" 
                                    rows="10" 
                                    id="output"
                                ></textarea>
                            </div>
                        </div>
                    </center>
                </form>    
            </div>
        );
    }
}

export default Editor;

















